package javaSintaxe;

public class Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Operators + - * / %
		int x = 5;
		int y = 11;
		
		int z = x + y;
		
		System.out.println(z);
		
		z = y - x;
		System.out.println(z);
		
		z = x * y;
		System.out.println(z);
		
		float w = (float) x / y;
		System.out.println(w);

		w = (2*6) + (4*4) + 2;
		System.out.println(w);
		
		z = y % x;
		System.out.println(z);
	}

}
